    public ListNode detectCycle(ListNode head) {
        for (ListNode fast = head, slow = head; fast != null && fast.next != null; ) {
            fast = fast.next.next;
            slow = slow.next;
            if (fast == slow) {
                for (fast = head; fast != slow; fast = fast.next, slow = slow.next);
                return slow;
            }
        }
        return null;
    }


