    public int subarraysDivByK(int[] nums, int k) {
        int ans = 0, prefix = 0;
        int[] freq = new int[k];
        freq[0] = 1;
        for (var x : nums) {
            prefix = (prefix + x%k + k) % k;
            ans += freq[prefix]++;
        }
        return ans;
    }


