                        int cnt1 = sz1, cnt2 = sz2;
                        if (freq1.getOrDefault(c1, 0) == 1) --cnt1;
                        if (freq1.getOrDefault(c2, 0) == 0) ++cnt1;
                        if (freq2.getOrDefault(c1, 0) == 0) ++cnt2;
