    public boolean checkArray(int[] nums, int k) {
        int prefix = 0;
        for (int i = 0; i < nums.length; ++i) {
            if (prefix > nums[i]) return false;
            int temp = nums[i];
            nums[i] -= prefix;
            prefix = temp;
            if (i >= k-1) prefix -= nums[i-k+1];
        }
        return prefix == 0;
    }


