    def minPathSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = [inf]*n
        ans[0] = 0
        for i in range(m):
            for j in range(n): 
                if j: ans[j] = min(ans[j-1], ans[j])
                ans[j] += grid[i][j]
        return ans[-1]


