    def widthOfBinaryTree(self, root: TreeNode) -> int:
        ans = 1
        queue = [(root, 0)]
        while queue: 
            ans = max(ans, queue[-1][1] - queue[0][1] + 1)
            newq = []
            for node, i in queue: 
                if node.left: newq.append((node.left, 2*i))
                if node.right: newq.append((node.right, 2*i+1))
            queue = newq
        return ans 


