    def similarRGB(self, color: str) -> str:
        
        def fn(c):
            """Return color of xx closest to input."""
            ans = 0
            for k in range(1, 16): 
                ans = min(ans, k*16+k, key=lambda x: abs(x - int(c, 16)))
