    def minNumberOfFrogs(self, croakOfFrogs: str) -> int:
        ans = 0
        freq = [0]*5 # freq array 
        for c in croakOfFrogs: 
            i = "croak".index(c)
            freq[i] += 1 
            if i and freq[i-1] < freq[i]: return -1 
