    def maximumUnits(self, boxTypes: List[List[int]], truckSize: int) -> int:
        ans = 0 
        for boxes, units in sorted(boxTypes, key=lambda x: x[1], reverse=True): 
            boxes = min(boxes, truckSize)
            ans += boxes * units
            truckSize -= boxes
        return ans 


